import React, { useState } from 'react';
import HeaderBar from './HeaderBar';
import Sidebar from './Sidebar';

function Layout({ children, messages = [], onSendMessage, onQuickAction, showChatBox = true, mode, onToggleTheme, onLogoClick, onNavigate }) {
  const [sidebar, setSidebar] = useState(false);
  const [showUploadModal, setShowUploadModal] = useState(false);

  return (
    <div style={{ 
      display: "flex", 
      height: "100vh", 
      background: "var(--bg)",
      color: "var(--fg)"
    }}>
      {/* Sidebar */}
      <Sidebar 
        open={sidebar} 
        onClose={() => setSidebar(false)} 
        onGo={(route) => {
          setSidebar(false);
          if (onNavigate) {
            onNavigate(route);
          }
        }} 
      />

      {/* Main Content Area */}
      <div style={{ 
        flex: 1, 
        display: "flex", 
        flexDirection: "column",
        overflow: "hidden"
      }}>
        {/* Header */}
        <HeaderBar
          onHamburger={() => setSidebar(true)}
          onToggleTheme={onToggleTheme}
          mode={mode}
          onLogoClick={onLogoClick}
        />

        {/* Content Area */}
        <div style={{ 
          flex: 1, 
          display: "flex", 
          flexDirection: "column",
          overflow: "hidden",
          marginTop: "64px" // Account for fixed header height
        }}>
          {/* Main Content */}
          <div style={{ 
            flex: 1, 
            overflow: "auto",
            padding: "16px 16px 80px 16px" // Extra bottom padding for fixed input bar
          }}>
            {children}
          </div>

          {/* Bottom Input Bar */}
          {showChatBox && (
            <div style={{ 
              position: "fixed",
              bottom: 0,
              left: 0,
              right: 0,
              background: "var(--bg)",
              borderTop: "1px solid var(--line)",
              padding: "16px",
              zIndex: 100
            }}>
              <div style={{ 
                display: "flex", 
                alignItems: "center", 
                gap: 12,
                maxWidth: "100%"
              }}>
                <div style={{ 
                  flex: 1,
                  position: "relative"
                }}>
                  <input
                    placeholder="Enter a prompt here..."
                    style={{ 
                      width: "100%",
                      background: "var(--card)", 
                      color: "var(--fg)", 
                      border: "1px solid var(--line)", 
                      padding: "12px 16px 12px 16px", 
                      borderRadius: 12,
                      fontSize: 14,
                      minHeight: 44,
                      outline: "none"
                    }}
                  />
                  <div style={{ 
                    position: "absolute",
                    right: 12,
                    top: "50%",
                    transform: "translateY(-50%)",
                    cursor: "pointer",
                    opacity: 0.6
                  }}>
                    🎤
                  </div>
                </div>

                {/* Upload Button */}
                <button 
                  onClick={() => setShowUploadModal(true)}
                  style={{ 
                    background: "var(--card)",
                    color: "var(--fg)", 
                    border: "1px solid var(--line)", 
                    padding: "12px", 
                    borderRadius: "50%", 
                    cursor: "pointer",
                    width: 44,
                    height: 44,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontSize: 16,
                    transition: "all 0.2s ease"
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.background = "var(--hover)";
                    e.target.style.transform = "scale(1.05)";
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.background = "var(--card)";
                    e.target.style.transform = "scale(1)";
                  }}
                >
                  📤
                </button>

                <button 
                  style={{ 
                    background: "#0039A6",
                    color: "white", 
                    border: 0, 
                    padding: "12px", 
                    borderRadius: "50%", 
                    cursor: "pointer",
                    width: 44,
                    height: 44,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontSize: 16
                  }}
                >
                  ✈️
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Layout;
