/**
 * Layout Component - Main application layout wrapper
 * 
 * Provides the overall structure for the GSU Panther Chatbot including:
 * - Header bar with logo and navigation
 * - Sidebar navigation menu
 * - Main content area
 * - Fixed bottom chat input bar
 * - Upload modal functionality
 * 
 * @param {Object} props - Component props
 * @param {React.ReactNode} props.children - Child components to render
 * @param {Array} props.messages - Chat messages array
 * @param {Function} props.onSendMessage - Function to handle sending messages
 * @param {Function} props.onQuickAction - Function to handle quick actions
 * @param {boolean} props.showChatBox - Whether to show the chat input bar
 * @param {string} props.mode - Current theme mode ('light' or 'dark')
 * @param {Function} props.onToggleTheme - Function to toggle theme
 * @param {Function} props.onLogoClick - Function to handle logo clicks
 * @param {Function} props.onNavigate - Function to handle navigation
 * 
 * @author GSU Software Engineering Team 6
 * @version 1.0.0
 */

import React, { useState, useEffect, useRef } from 'react';
import HeaderBar from './HeaderBar';
import Sidebar from './Sidebar';
import supabaseService from '../services/supabaseService';

function Layout({
  children,
  messages = [],
  onSendMessage,
  onQuickAction,
  showChatBox = true,
  mode,
  isTyping = false,
  onToggleTheme,
  onLogoClick,
  onNavigate,
  onNewChat
}) {
  // Component state
  const [sidebar, setSidebar] = useState(false); // Controls sidebar visibility
  const [showUploadModal, setShowUploadModal] = useState(false); // Controls upload modal visibility
  const [inputMessage, setInputMessage] = useState(""); // Current input message
  const [isUserScrolling, setIsUserScrolling] = useState(false); // Track if user is manually scrolling
  const [shouldAutoScroll, setShouldAutoScroll] = useState(true); // Whether to auto-scroll
  const [hasNewMessages, setHasNewMessages] = useState(false); // Track if there are new messages when user scrolled up
  const [selectedFiles, setSelectedFiles] = useState([]); // Selected files for upload
  const [isUploading, setIsUploading] = useState(false); // Upload state
  const [uploadProgress, setUploadProgress] = useState(0); // Upload progress
  const chatAreaRef = useRef(null); // Reference to chat area for auto-scroll
  const fileInputRef = useRef(null); // Reference to file input

  // Check if user is near the bottom of the chat
  const isNearBottom = () => {
    if (!chatAreaRef.current) return true;
    const { scrollTop, scrollHeight, clientHeight } = chatAreaRef.current;
    return scrollHeight - scrollTop - clientHeight < 50; // Within 50px of bottom (more sensitive)
  };

  // Handle scroll events to detect manual scrolling
  const handleScroll = () => {
    if (!chatAreaRef.current) return;

    const nearBottom = isNearBottom();
    setShouldAutoScroll(nearBottom);

    // If user scrolls to bottom, re-enable auto-scroll
    if (nearBottom) {
      setIsUserScrolling(false);
      setHasNewMessages(false);
    } else {
      setIsUserScrolling(true);
    }
  };

  // Function to scroll to bottom
  const scrollToBottom = () => {
    if (chatAreaRef.current) {
      chatAreaRef.current.scrollTo({
        top: chatAreaRef.current.scrollHeight,
        behavior: 'smooth'
      });
      setShouldAutoScroll(true);
      setIsUserScrolling(false);
      setHasNewMessages(false);
    }
  };

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    if (chatAreaRef.current) {
      // Always scroll to bottom when new messages arrive
      chatAreaRef.current.scrollTop = chatAreaRef.current.scrollHeight;

      // Reset scroll states to ensure we stay at bottom
      setShouldAutoScroll(true);
      setIsUserScrolling(false);
      setHasNewMessages(false);
    }
  }, [messages, isTyping]); // Also scroll when typing indicator changes

  // Handle sending messages
  const handleSendMessage = () => {
    if (inputMessage.trim() && onSendMessage) {
      onSendMessage({
        id: Math.random().toString(36).slice(2),
        role: "user",
        text: inputMessage.trim()
      });
      setInputMessage("");

      // Force scroll to bottom when user sends a message
      setTimeout(() => {
        if (chatAreaRef.current) {
          chatAreaRef.current.scrollTop = chatAreaRef.current.scrollHeight;
          setShouldAutoScroll(true);
          setIsUserScrolling(false);
          setHasNewMessages(false);
        }
      }, 50); // Reduced delay for faster response

      // Also scroll immediately
      if (chatAreaRef.current) {
        chatAreaRef.current.scrollTop = chatAreaRef.current.scrollHeight;
      }
    }
  };

  // Handle Enter key press
  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Handle file selection
  const handleFileSelect = (event) => {
    const files = Array.from(event.target.files);
    setSelectedFiles(files);
  };

  // Handle file upload
  const handleFileUpload = async () => {
    if (selectedFiles.length === 0) return;

    setIsUploading(true);
    setUploadProgress(0);

    try {
      const uploadResults = [];

      for (let i = 0; i < selectedFiles.length; i++) {
        const file = selectedFiles[i];
        console.log('Uploading file from chat:', file.name);

        const result = await supabaseService.uploadFile(file, 'default_user', 'chat_uploads');

        if (result.success) {
          uploadResults.push({
            name: file.name,
            url: result.publicUrl,
            size: supabaseService.formatFileSize(file.size),
            type: supabaseService.getFileType(file.name)
          });
          setUploadProgress(((i + 1) / selectedFiles.length) * 100);
        } else {
          console.error('Upload failed:', result.error);
          alert(`Failed to upload ${file.name}: ${result.error}`);
        }
      }

      // Create a message about the uploaded files
      if (uploadResults.length > 0 && onSendMessage) {
        const fileList = uploadResults.map(file => `ðŸ“Ž ${file.name} (${file.size})`).join('\n');
        const uploadMessage = `I've uploaded ${uploadResults.length} file${uploadResults.length > 1 ? 's' : ''}:\n\n${fileList}\n\nPlease analyze these files and help me with any questions I have about them.`;

        onSendMessage({
          id: Math.random().toString(36).slice(2),
          role: "user",
          text: uploadMessage,
          attachments: uploadResults
        });
      }

      // Reset form
      setSelectedFiles([]);
      setShowUploadModal(false);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }

    } catch (error) {
      console.error('Upload error:', error);
      alert('Upload failed: ' + error.message);
    } finally {
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  // Handle drag and drop
  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const files = Array.from(e.dataTransfer.files);
    setSelectedFiles(files);
  };

  return (
    <>
      <div style={{
        display: "flex",
        height: "100vh",
        background: "var(--bg)",
        color: "var(--fg)"
      }}>
        {/* Sidebar */}
        <Sidebar
          open={sidebar}
          onClose={() => setSidebar(false)}
          onGo={(route) => {
            setSidebar(false);
            if (onNavigate) {
              onNavigate(route);
            }
          }}
        />

        {/* Main Content Area */}
        <div style={{
          flex: 1,
          display: "flex",
          flexDirection: "column",
          overflow: "hidden"
        }}>
          {/* Header */}
          <HeaderBar
            onHamburger={() => setSidebar(true)}
            onToggleTheme={onToggleTheme}
            mode={mode}
            onLogoClick={onLogoClick}
          />

          {/* Content Area */}
          <div style={{
            flex: showChatBox ? "none" : 1,
            display: "flex",
            flexDirection: "column",
            overflow: "hidden",
            marginTop: "64px", // Account for fixed header height
            height: showChatBox ? "calc(100vh - 64px)" : "auto" // Explicit height for chat mode
          }}>
            {/* Main Content - Only show when not in chat mode */}
            {!showChatBox && (
              <div style={{
                flex: 1,
                overflow: "auto",
                padding: "16px 16px 0 16px"
              }}>
                {children}
              </div>
            )}

            {/* Full Chat Experience - Takes up all space when chat is active */}
            {showChatBox && (
              <div
                ref={chatAreaRef}
                onScroll={handleScroll}
                style={{
                  height: "calc(100vh - 64px - 80px)", // Full height minus header (64px) minus input bar (80px)
                  background: "var(--bg)",
                  overflow: "auto",
                  padding: "16px",
                  display: "flex",
                  flexDirection: "column"
                }}
              >
                {/* Recent History Section - Embedded in chat */}
                <div style={{
                  background: "var(--card)",
                  borderRadius: 16,
                  padding: 20,
                  marginBottom: 16,
                  border: "1px solid var(--line)"
                }}>
                  <h3 style={{
                    margin: "0 0 16px 0",
                    fontSize: "1.2rem",
                    fontWeight: "bold",
                    color: "var(--fg)",
                    display: "flex",
                    alignItems: "center",
                    gap: 8
                  }}>
                    ðŸ“š Recent History
                  </h3>

                  <div style={{ display: "flex", flexDirection: "column", gap: 12 }}>
                    <div
                      onClick={() => {
                        if (onSendMessage) {
                          onSendMessage({
                            id: Math.random().toString(36).slice(2),
                            role: "user",
                            text: "Help me plan my next semester courses"
                          });
                        }
                      }}
                      style={{
                        background: "var(--hover)",
                        borderRadius: 12,
                        padding: 16,
                        border: "1px solid var(--line)",
                        display: "flex",
                        alignItems: "center",
                        gap: 12,
                        cursor: "pointer",
                        transition: "all 0.2s ease"
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = "var(--line)";
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = "var(--hover)";
                      }}
                    >
                      <div style={{
                        width: 40,
                        height: 40,
                        background: "#0039A620",
                        borderRadius: "50%",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        fontSize: "1.2rem"
                      }}>
                        ðŸ’¬
                      </div>
                      <div style={{ flex: 1 }}>
                        <p style={{ margin: "0 0 4px 0", fontSize: "0.95rem", color: "var(--fg)", fontWeight: 600 }}>
                          Help me plan my next semester courses
                        </p>
                        <p style={{ margin: 0, fontSize: "0.8rem", opacity: 0.7 }}>
                          Academic planning â€¢ 2 days ago
                        </p>
                      </div>
                      <div style={{
                        width: 24,
                        height: 24,
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        cursor: "pointer",
                        opacity: 0.6
                      }}>
                        â‹®
                      </div>
                    </div>

                    <div
                      onClick={() => {
                        if (onSendMessage) {
                          onSendMessage({
                            id: Math.random().toString(36).slice(2),
                            role: "user",
                            text: "What campus resources are available?"
                          });
                        }
                      }}
                      style={{
                        background: "var(--hover)",
                        borderRadius: 12,
                        padding: 16,
                        border: "1px solid var(--line)",
                        display: "flex",
                        alignItems: "center",
                        gap: 12,
                        cursor: "pointer",
                        transition: "all 0.2s ease"
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.background = "var(--line)";
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.background = "var(--hover)";
                      }}
                    >
                      <div style={{
                        width: 40,
                        height: 40,
                        background: "#0039A620",
                        borderRadius: "50%",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        fontSize: "1.2rem"
                      }}>
                        ðŸŽ¤
                      </div>
                      <div style={{ flex: 1 }}>
                        <p style={{ margin: "0 0 4px 0", fontSize: "0.95rem", color: "var(--fg)", fontWeight: 600 }}>
                          What campus resources are available?
                        </p>
                        <p style={{ margin: 0, fontSize: "0.8rem", opacity: 0.7 }}>
                          Campus resources â€¢ 1 week ago
                        </p>
                      </div>
                      <div style={{
                        width: 24,
                        height: 24,
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        cursor: "pointer",
                        opacity: 0.6
                      }}>
                        â‹®
