import React, { useState, useRef } from 'react';
import Card from '../components/Card';
import { GSU } from '../constants/colors';

function ScreenChat({ onOpenVoice }) {
  const [messages, setMessages] = useState([
    { id: "1", role: "bot", text: "Hi! I'm Pounce. Want me to scan your transcript or plan next semester?" },
  ]);
  const [draft, setDraft] = useState("");
  const fileRef = useRef(null);

  const send = (msg) =>
    setMessages((m) => [...m, { id: Math.random().toString(36).slice(2), ...msg }]);

  const onSubmit = (e) => {
    e.preventDefault();
    if (!draft.trim()) return;
    send({ role: "user", text: draft.trim() });
    setDraft("");
    setTimeout(() => send({ role: "bot", text: "Cool. I'll pull your requirements and draft a pack." }), 600);
  };

  const onPick = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    const url = URL.createObjectURL(f);
    send({ role: "user", image: url });
  };

  return (
    <div style={{ display: "grid", gap: 12 }}>
      {/* Pounce area – replace with 3D panel when ready */}
      <Card style={{ padding: 0, overflow: "hidden" }}>
        <div
          role="button"
          aria-label="Open voice assistant"
          onClick={onOpenVoice}
          style={{
            height: 240,
            background: `linear-gradient(135deg, ${GSU.blue} 0%, #1e3f7f 100%)`,
            display: "grid",
            gridTemplateColumns: "96px 1fr",
            alignItems: "center",
            gap: 12,
            cursor: "pointer",
          }}
        >
          <div style={{ display: "grid", placeItems: "center", height: "100%", background: "rgba(255,255,255,0.06)" }}>
            <img
              src="https://upload.wikimedia.org/wikipedia/en/0/0c/Georgia_State_Panthers_logo.svg"
              alt="Pounce"
              style={{ width: 72, height: 72, filter: "drop-shadow(0 8px 20px rgba(0,0,0,0.35))" }}
            />
          </div>
          <div style={{ color: "white", paddingRight: 16 }}>
            <h3 style={{ margin: 0 }}>Pounce is ready</h3>
            <p style={{ margin: "6px 0 0", opacity: 0.9 }}>Tap here to speak—your voice will be transcribed and answered in chat.</p>
          </div>
        </div>
        <div style={{ padding: 10, display: "flex", alignItems: "center", gap: 8, borderTop: "1px solid var(--line)" }}>
          <span style={{ width: 8, height: 8, borderRadius: 999, background: "#17cf6e" }} />
          <span style={{ color: "var(--fg)", opacity: 0.8 }}>Online • Tap the banner to use voice</span>
        </div>
      </Card>

      {/* Chat window */}
      <Card style={{ display: "flex", flexDirection: "column", gap: 12, height: 420 }}>
        <div style={{ fontWeight: 700 }}>Chat</div>
        <div style={{ flex: 1, overflowY: "auto", display: "grid", gap: 10 }}>
          {messages.map((m) => (
            <div key={m.id} style={{ justifySelf: m.role === "user" ? "end" : "start", maxWidth: 560 }}>
              <div
                style={{
                  background: m.role === "user" ? "var(--bubbleUser)" : "var(--bubbleBot)",
                  color: m.role === "user" ? "white" : "var(--fg)",
                  border: "1px solid var(--line)",
                  borderRadius: 16,
                  padding: 12,
                  boxShadow: "0 8px 16px rgba(0,0,0,0.15)",
                }}
              >
                {m.text && <p style={{ margin: 0 }}>{m.text}</p>}
                {m.image && (
                  <img 
                    src={m.image} 
                    alt="upload" 
                    style={{ 
                      display: "block", 
                      width: "100%", 
                      borderRadius: 12, 
                      border: "1px solid var(--line)", 
                      marginTop: m.text ? 8 : 0 
                    }} 
                  />
                )}
              </div>
            </div>
          ))}
        </div>
        <form onSubmit={onSubmit} style={{ display: "flex", gap: 8 }}>
          <button 
            type="button" 
            onClick={() => fileRef.current?.click()}
            style={{ 
              background: "transparent", 
              color: "var(--fg)", 
              border: `1px solid var(--line)`, 
              padding: "10px 12px", 
              borderRadius: 12, 
              cursor: "pointer" 
            }}
          >
            📷 Upload
          </button>
          <input ref={fileRef} type="file" accept="image/*" hidden onChange={onPick} />
          <input
            value={draft}
            onChange={(e) => setDraft(e.target.value)}
            placeholder="Type your message…"
            style={{ 
              flex: 1, 
              background: "transparent", 
              color: "var(--fg)", 
              border: `1px solid var(--line)`, 
              padding: "10px 12px", 
              borderRadius: 12 
            }}
          />
          <button 
            type="submit" 
            style={{ 
              background: GSU.blue, 
              color: "white", 
              border: 0, 
              padding: "10px 16px", 
              borderRadius: 12, 
              fontWeight: 700, 
              cursor: "pointer" 
            }}
          >
            Send
          </button>
        </form>
      </Card>
    </div>
  );
}

export default ScreenChat;
