import React, { useState } from 'react';
import HeaderBar from './HeaderBar';
import Sidebar from './Sidebar';
import ChatBox from './ChatBox';

function Layout({ children, messages = [], onSendMessage, onQuickAction, showChatBox = true, mode, onToggleTheme }) {
  const [sidebar, setSidebar] = useState(false);

  return (
    <div style={{ 
      display: "flex", 
      height: "100vh", 
      background: "var(--bg)",
      color: "var(--fg)"
    }}>
      {/* Sidebar */}
      <Sidebar 
        open={sidebar} 
        onClose={() => setSidebar(false)} 
        onGo={(route) => {
          setSidebar(false);
          // Handle navigation here if needed
        }} 
      />

      {/* Main Content Area */}
      <div style={{ 
        flex: 1, 
        display: "flex", 
        flexDirection: "column",
        overflow: "hidden"
      }}>
        {/* Header */}
        <HeaderBar
          onHamburger={() => setSidebar(true)}
          onToggleTheme={onToggleTheme}
          mode={mode}
        />

        {/* Content Area */}
        <div style={{ 
          flex: 1, 
          display: "flex", 
          overflow: "hidden"
        }}>
          {/* Main Content */}
          <div style={{ 
            flex: showChatBox ? 1 : 1, 
            overflow: "auto",
            padding: 16
          }}>
            {children}
          </div>

          {/* Chat Box Sidebar */}
          {showChatBox && (
            <div style={{ 
              width: 400, 
              borderLeft: "1px solid var(--line)",
              background: "var(--bg)",
              display: "flex",
              flexDirection: "column"
            }}>
              <div style={{ 
                padding: 16, 
                borderBottom: "1px solid var(--line)",
                background: "var(--card)"
              }}>
                <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
                  <img
                    src="https://upload.wikimedia.org/wikipedia/en/0/0c/Georgia_State_Panthers_logo.svg"
                    alt="Pounce"
                    style={{ width: 32, height: 32 }}
                  />
                  <div>
                    <h3 style={{ margin: 0, fontSize: 16 }}>Pounce Assistant</h3>
                    <div style={{ 
                      display: "flex", 
                      alignItems: "center", 
                      gap: 6,
                      fontSize: 12,
                      opacity: 0.8
                    }}>
                      <span style={{ 
                        width: 6, 
                        height: 6, 
                        borderRadius: 999, 
                        background: "#17cf6e" 
                      }} />
                      Online
                    </div>
                  </div>
                </div>
              </div>

              <div style={{ flex: 1, padding: 16 }}>
                <ChatBox 
                  messages={messages}
                  onSendMessage={onSendMessage}
                  onQuickAction={onQuickAction}
                  style={{ height: "100%" }}
                />
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Layout;
