import React from 'react';
import { GSU } from '../constants/colors';

function ScreenHome({ onStart, onGoChat, onGoToFiles, onGoToHistory }) {
  return (
    <div style={{ 
      display: "flex", 
      flexDirection: "column", 
      gap: 20,
      height: "100%",
      width: "100%",
      padding: "0 16px"
    }}>
      {/* App Title Section */}
      <div style={{ 
        textAlign: "center",
        padding: "20px 0 16px 0"
      }}>
        <h1 style={{ 
          margin: "0 0 8px 0", 
          fontSize: "2rem", 
          fontWeight: "bold", 
          color: "var(--fg)" 
        }}>
          GSU Panther Chat
        </h1>
        <p style={{ 
          margin: "0 0 12px 0", 
          opacity: 0.7, 
          fontSize: "1rem" 
        }}>
          Your AI-powered academic assistant
        </p>
        {/* FBX Model Container */}
        <div style={{
          width: "100%",
          maxWidth: "800px",
          height: "400px",
          margin: "0 auto",
          background: "var(--card)",
          borderRadius: 16,
          border: "2px solid var(--line)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          position: "relative",
          overflow: "hidden",
          boxShadow: "0 8px 32px rgba(0,0,0,0.1)"
        }}>
          {/* FBX Model Placeholder */}
          <div style={{
            width: "100%",
            height: "100%",
            background: "linear-gradient(135deg, var(--bg) 0%, var(--hover) 100%)",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            position: "relative"
          }}>
            {/* 3D Model Icon */}
            <div style={{
              fontSize: "4rem",
              marginBottom: 16,
              opacity: 0.8
            }}>
              🎯
            </div>

            {/* Loading Indicator */}
            <div style={{
              display: "flex",
              alignItems: "center",
              gap: 8,
              marginBottom: 12
            }}>
              <div style={{
                width: 8,
                height: 8,
                background: GSU.blue,
                borderRadius: "50%",
                animation: "pulse 1.5s infinite ease-in-out"
              }} />
              <div style={{
