import React from 'react';
import Card from '../components/Card';
import { GSU } from '../constants/colors';

const chipBtn = {
  border: `1px solid var(--line)`,
  background: "transparent",
  color: "var(--fg)",
  borderRadius: 999,
  padding: "8px 12px",
  cursor: "pointer",
};

function ScreenHome({ onStart, onGoChat }) {
  return (
    <div style={{ 
      display: "flex", 
      flexDirection: "column", 
      gap: 24,
      height: "100%",
      justifyContent: "center",
      alignItems: "center",
      textAlign: "center",
      padding: "40px 20px"
    }}>
      {/* Hero Section */}
      <div style={{ maxWidth: 600 }}>
        <img
          src="https://upload.wikimedia.org/wikipedia/en/0/0c/Georgia_State_Panthers_logo.svg"
          alt="GSU Panther"
          style={{ width: 120, height: 120, marginBottom: 24 }}
        />
        <h1 style={{ 
          margin: "0 0 16px 0", 
          fontSize: "2.5rem",
          fontWeight: 700,
          background: `linear-gradient(135deg, ${GSU.blue} 0%, #1e3f7f 100%)`,
          WebkitBackgroundClip: "text",
          WebkitTextFillColor: "transparent",
          backgroundClip: "text"
        }}>
          Meet Pounce Assistant
        </h1>
        <p style={{ 
          margin: "0 0 32px 0", 
          fontSize: "1.2rem",
          opacity: 0.8,
          lineHeight: 1.6
        }}>
          Your digital guide to advising, course planning, deadlines, and campus life at Georgia State University.
        </p>

        <div style={{ display: "flex", gap: 16, justifyContent: "center", flexWrap: "wrap", marginBottom: 32 }}>
          <button
            onClick={onStart}
            style={{
              background: GSU.blue,
              color: "white",
              border: 0,
              padding: "16px 24px",
              borderRadius: 16,
              fontWeight: 700,
              cursor: "pointer",
              fontSize: "1.1rem",
              boxShadow: "0 8px 24px rgba(0, 57, 166, 0.3)",
              transition: "all 0.2s ease"
            }}
            onMouseOver={(e) => {
              e.target.style.transform = "translateY(-2px)";
              e.target.style.boxShadow = "0 12px 32px rgba(0, 57, 166, 0.4)";
            }}
            onMouseOut={(e) => {
              e.target.style.transform = "translateY(0)";
              e.target.style.boxShadow = "0 8px 24px rgba(0, 57, 166, 0.3)";
            }}
          >
            Start New Conversation
          </button>
          <button
            onClick={onGoChat}
            style={{
              background: "transparent",
              color: "var(--fg)",
              border: `2px solid var(--line)`,
              padding: "16px 24px",
              borderRadius: 16,
              cursor: "pointer",
              fontSize: "1.1rem",
              fontWeight: 600,
              transition: "all 0.2s ease"
            }}
            onMouseOver={(e) => {
              e.target.style.borderColor = GSU.blue;
              e.target.style.color = GSU.blue;
            }}
            onMouseOut={(e) => {
              e.target.style.borderColor = "var(--line)";
              e.target.style.color = "var(--fg)";
            }}
          >
            Explore Features
          </button>
        </div>
      </div>

      {/* Features Grid */}
      <div style={{ 
        display: "grid", 
        gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))", 
        gap: 20,
        maxWidth: 800,
        width: "100%"
      }}>
        <Card style={{ textAlign: "left" }}>
          <div style={{ fontSize: "2rem", marginBottom: 12 }}>🎓</div>
          <h3 style={{ margin: "0 0 8px 0" }}>Academic Advising</h3>
          <p style={{ margin: 0, opacity: 0.8, fontSize: "0.95rem" }}>
            Get personalized course recommendations and degree planning assistance.
          </p>
        </Card>

        <Card style={{ textAlign: "left" }}>
          <div style={{ fontSize: "2rem", marginBottom: 12 }}>📅</div>
          <h3 style={{ margin: "0 0 8px 0" }}>Schedule Planning</h3>
          <p style={{ margin: 0, opacity: 0.8, fontSize: "0.95rem" }}>
            Build optimal class schedules that fit your preferences and requirements.
          </p>
        </Card>

        <Card style={{ textAlign: "left" }}>
          <div style={{ fontSize: "2rem", marginBottom: 12 }}>🎤</div>
          <h3 style={{ margin: "0 0 8px 0" }}>Voice Assistant</h3>
          <p style={{ margin: 0, opacity: 0.8, fontSize: "0.95rem" }}>
            Speak naturally with Pounce using advanced speech recognition.
          </p>
        </Card>
      </div>

      {/* Quick Actions */}
      <div style={{ maxWidth: 600, width: "100%" }}>
        <h3 style={{ margin: "0 0 16px 0", fontSize: "1.3rem" }}>Quick Actions</h3>
        <div style={{ display: "flex", gap: 12, flexWrap: "wrap", justifyContent: "center" }}>
          <button style={chipBtn} onClick={onGoChat}>Upload Transcripts</button>
          <button style={chipBtn} onClick={onGoChat}>Check Degree Audit</button>
          <button style={chipBtn} onClick={onGoChat}>Find Resources</button>
          <button style={chipBtn} onClick={onGoChat}>Campus Events</button>
        </div>
      </div>
    </div>
  );
}

export default ScreenHome;
