import { createClient } from '@supabase/supabase-js';

// Initialize Supabase client
const supabaseUrl = process.env.REACT_APP_SUPABASE_URL || 'https://xmdfjheusbiqklrqxgow.supabase.co';
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY || 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6InhtZGZqaGV1c2JpcWtscnF4Z293Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTkyNDkzMzUsImV4cCI6MjA3NDgyNTMzNX0.3GsHStv3AqLjWIAZjL8hY4ESEJ-T4MdIpVkebzJ2aaI';

const supabase = createClient(supabaseUrl, supabaseAnonKey);

export const supabaseService = {
    /**
     * Upload a file to the User_Files bucket
     * @param {File} file - The file to upload
     * @param {string} userId - User ID for organizing files
     * @param {string} category - Category for organizing files (academic, schedule, documents, resources)
     * @returns {Promise<Object>} Upload result with file path and URL
     */
    async uploadFile(file, userId = 'default_user', category = 'documents') {
        try {
      // Generate unique filename with timestamp
      const timestamp = new Date().getTime();
      const fileName = `${userId}/${category}/${timestamp}_${file.name.replace(/[^a-zA-Z0-9.-]/g, '_')}`;            console.log('Uploading file:', fileName);

            // Upload file to Supabase Storage
            const { data, error } = await supabase.storage
                .from('User_Files')
                .upload(fileName, file, {
                    cacheControl: '3600',
                    upsert: false
                });

            if (error) {
                console.error('Upload error:', error);
                throw new Error(`Upload failed: ${error.message}`);
            }

            // Get public URL for the uploaded file
            const { data: urlData } = supabase.storage
                .from('User_Files')
                .getPublicUrl(fileName);

            console.log('File uploaded successfully:', data);

            return {
                success: true,
                filePath: data.path,
                publicUrl: urlData.publicUrl,
                fileName: file.name,
                fileSize: file.size,
                fileType: file.type || 'application/octet-stream',
                uploadedAt: new Date().toISOString(),
                category
            };

        } catch (error) {
            console.error('Supabase upload error:', error);
            return {
                success: false,
                error: error.message
            };
        }
    },

    /**
     * List files from the User_Files bucket
     * @param {string} userId - User ID to filter files
     * @param {string} category - Optional category filter
     * @returns {Promise<Array>} List of files
     */
    async listFiles(userId = 'default_user', category = null) {
        try {
            const folderPath = category ? `${userId}/${category}` : userId;

            const { data, error } = await supabase.storage
                .from('User_Files')
                .list(folderPath, {
                    limit: 100,
                    offset: 0,
                    sortBy: { column: 'created_at', order: 'desc' }
                });

            if (error) {
                throw new Error(`Failed to list files: ${error.message}`);
            }

            // Get public URLs for each file
            const filesWithUrls = data.map(file => {
                const fullPath = `${folderPath}/${file.name}`;
                const { data: urlData } = supabase.storage
                    .from('User_Files')
                    .getPublicUrl(fullPath);

                return {
                    name: file.name,
                    path: fullPath,
                    size: file.metadata?.size || 0,
                    lastModified: file.updated_at,
                    publicUrl: urlData.publicUrl,
                    type: this.getFileType(file.name)
                };
            });

            return {
                success: true,
                files: filesWithUrls
            };

        } catch (error) {
            console.error('Supabase list error:', error);
            return {
                success: false,
                error: error.message,
                files: []
            };
        }
    },

    /**
     * Delete a file from the User_Files bucket
     * @param {string} filePath - Full path to the file
     * @returns {Promise<Object>} Delete result
     */
    async deleteFile(filePath) {
    try {
      const { error } = await supabase.storage
        .from('User_Files')
        .remove([filePath]);

      if (error) {
        throw new Error(`Failed to delete file: ${error.message}`);
      }            return {
                success: true,
                message: 'File deleted successfully'
            };

        } catch (error) {
            console.error('Supabase delete error:', error);
            return {
                success: false,
                error: error.message
            };
        }
    },

    /**
     * Get file type based on extension
     * @param {string} filename - Name of the file
     * @returns {string} File type
     */
    getFileType(filename) {
        const extension = filename.split('.').pop()?.toLowerCase();
        switch (extension) {
            case 'pdf': return 'PDF';
            case 'doc':
            case 'docx': return 'DOC';
            case 'xls':
            case 'xlsx': return 'XLS';
            case 'ppt':
            case 'pptx': return 'PPT';
            case 'txt': return 'TXT';
            case 'jpg':
            case 'jpeg':
            case 'png':
            case 'gif': return 'IMG';
            default: return 'FILE';
        }
    },

    /**
     * Format file size for display
     * @param {number} bytes - File size in bytes
     * @returns {string} Formatted file size
     */
    formatFileSize(bytes) {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    }
};

export default supabaseService;
