import React, { useState } from 'react';
import { useTheme } from './hooks/useTheme';
import HeaderBar from './components/HeaderBar';
import Sidebar from './components/Sidebar';
import ScreenHome from './screens/ScreenHome';
import ScreenChat from './screens/ScreenChat';
import ScreenVoice from './screens/ScreenVoice';

function App() {
  const { mode, setMode, vars } = useTheme();
  const [sidebar, setSidebar] = useState(false);
  const [screen, setScreen] = useState("home");

  // simple route helpers
  const go = (s) => {
    setSidebar(false);
    setScreen(s);
  };

  return (
    <div style={{ 
      ...vars, 
      minHeight: "100vh", 
      background: "var(--bg)", 
      color: "var(--fg)", 
      transition: "background 180ms ease, color 180ms ease" 
    }}>
      <HeaderBar
        onHamburger={() => setSidebar(true)}
        onToggleTheme={() => setMode(mode === "dark" ? "light" : "dark")}
        mode={mode}
      />

      <main
        className="safe"
        style={{
          maxWidth: 820,
          margin: "0 auto",
          padding: 16,
          display: "grid",
          gap: 16,
        }}
      >
        {screen === "home" && (
          <ScreenHome onStart={() => go("chat")} onGoChat={() => go("chat")} />
        )}
        {screen === "chat" && <ScreenChat onOpenVoice={() => go("voice")} />}
        {screen === "voice" && <ScreenVoice onBackToChat={() => go("chat")} />}
      </main>

      <Sidebar open={sidebar} onClose={() => setSidebar(false)} onGo={(r) => go(r)} />

      <footer style={{ textAlign: "center", padding: 12, opacity: 0.7, fontSize: 12 }}>
        GSU Panther Chat – demo • Blue/Black & Blue/White • © {new Date().getFullYear()}
      </footer>
    </div>
  );
}

export default App;
