import React from 'react';

function HeaderBar({ onHamburger, onToggleTheme, mode, onLogoClick }) {
  return (
    <div
      style={{
        position: "sticky",
        top: 0,
        zIndex: 10,
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "12px 14px",
        background: "var(--bg)",
        color: "var(--fg)",
        borderBottom: "1px solid var(--line)",
      }}
    >
      <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
        <button
          onClick={onHamburger}
          aria-label="Open menu"
          style={{
            width: 40,
            height: 40,
            borderRadius: 12,
            border: `1px solid var(--line)`,
            background: "transparent",
            color: "var(--fg)",
            display: "grid",
            placeItems: "center",
            fontSize: 20,
            cursor: "pointer",
          }}
        >
          ‚â°
        </button>
        <div 
          onClick={onLogoClick}
          style={{ 
            display: "flex", 
            alignItems: "center", 
            gap: 12,
            cursor: "pointer",
            padding: "4px 8px",
            borderRadius: 8,
            transition: "background 0.2s ease"
          }}
          onMouseEnter={(e) => {
            e.target.style.background = "var(--hover)";
          }}
          onMouseLeave={(e) => {
            e.target.style.background = "transparent";
          }}
        >
          <img 
            src="/gsu-panther-logo.png" 
            alt="GSU Panther" 
            width={40} 
            height={40} 
          />
          <strong>GSU Panther Chat</strong>
        </div>
      </div>
      <button
        onClick={onToggleTheme}
        aria-label="Toggle theme"
        style={{
          width: 40,
          height: 40,
          borderRadius: 12,
          border: `1px solid var(--line)`,
          background: "transparent",
          color: "var(--fg)",
          display: "grid",
          placeItems: "center",
          fontSize: 14,
          cursor: "pointer",
          fontWeight: 700,
        }}
      >
        {mode === "dark" ? "üåô" : "‚òÄÔ∏è"}
      </button>
    </div>
  );
}

export default HeaderBar;
